DROP TABLE IF EXISTS `dnii8_finder_filters`;
CREATE TABLE `dnii8_finder_filters` (
  `filter_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
